import React, { useRef, useEffect } from 'react';
import { useFrame, useLoader } from '@react-three/fiber';
import * as THREE from 'three';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader';
import { TextGeometry } from 'three/examples/jsm/geometries/TextGeometry';
import circleImg from '../assets/circle.png';

function ParticleText({ text }) {
  const meshRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const font = useLoader(FontLoader, 'src/assets/Baskervville_Regular.json'); // フォントファイルのパスを指定

  useEffect(() => {
    if (font && meshRef.current) {
      const geometry = new TextGeometry(text, {
        font: font,
        size: 1,
        height: 0.2,
        curveSegments: 12,
        bevelEnabled: false,
      });
      geometry.center();
      const points = geometry.toNonIndexed().attributes.position.array;
      const positions = [];
      for (let i = 0; i < points.length; i += 3) {
        positions.push(points[i], points[i + 1], points[i + 2]);
      }
      meshRef.current.geometry.setAttribute(
        'position',
        new THREE.BufferAttribute(new Float32Array(positions), 3)
      );
    }
  }, [font, text]);

  useFrame(({ clock }) => {
    meshRef.current.rotation.y += 0.001;
  });

  return (
    <points ref={meshRef}>
      <bufferGeometry />
      <pointsMaterial
        map={imgTex}
        color={0xffa500}
        size={0.05}
        sizeAttenuation
        transparent={true}
        opacity={0.8}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

export default ParticleText;
