// ./src/App.js
import './App.css';
import * as THREE from 'three';
import { Suspense, useCallback, useMemo, useRef } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import circleImg from './assets/circle.png';

function CameraControls() {
  return <OrbitControls autoRotate autoRotateSpeed={-0.2} />;
}

function Points() {
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const bufferRef = useRef();
  const tRef = useRef(0); // useRef を使用して t を管理

  const f = 0.002;
  const a = 3;
  const graph = useCallback((x, z) => {
    return Math.sin(f * (x ** 2 + z ** 2 + tRef.current)) * a;
  }, [f, a]);

  const count = 50; // ポイント数を減らす
  const sep = 3; // 間隔を調整
  const positions = useMemo(() => {
    const positions = [];

    for (let xi = 0; xi < count; xi++) {
      for (let zi = 0; zi < count; zi++) {
        const x = sep * (xi - count / 2);
        const z = sep * (zi - count / 2);
        const y = graph(x, z);
        positions.push(x, y, z);
      }
    }

    return new Float32Array(positions);
  }, [count, sep, graph]);

  useFrame(() => {
    tRef.current += 0.15; // t を更新（値を小さく）

    const positions = bufferRef.current.array;

    let i = 0;
    for (let xi = 0; xi < count; xi++) {
      for (let zi = 0; zi < count; zi++) {
        const x = sep * (xi - count / 2);
        const z = sep * (zi - count / 2);

        positions[i + 1] = graph(x, z);
        i += 3;
      }
    }

    bufferRef.current.needsUpdate = true;
  });

  return (
    <points>
      <bufferGeometry attach="geometry">
        <bufferAttribute
          ref={bufferRef}
          attachObject={['attributes', 'position']}
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>

      <pointsMaterial
        attach="material"
        // map={imgTex} // デバッグ用に一時的に無効化
        color={0xff0000} // 赤色に変更
        size={1.0} // サイズを大きく
        sizeAttenuation
        transparent={false}
        alphaTest={0.5}
        opacity={1.0}
      />
    </points>
  );
}

function AnimationCanvas() {
  return (
    <Canvas
      camera={{ position: [0, 100, 100], fov: 60 }}
      style={{ width: '100%', height: '100%', background: '#000000' }}
    >
      <Suspense fallback={null}>
        <Points />
      </Suspense>
      <CameraControls />
      {/* AxesHelper と GridHelper を追加 */}
      <primitive object={new THREE.AxesHelper(50)} />
      <primitive object={new THREE.GridHelper(200, 50)} />
    </Canvas>
  );
}

function App() {
  return (
    <div className="anim">
      <Suspense fallback={<div>Loading...</div>}>
        <AnimationCanvas />
      </Suspense>
    </div>
  );
}

export default App;
