// App.js
import React, { useRef, useMemo } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';
import circleImg from './assets/circle.png'; // テクスチャのパスを正しく設定
import './App.css';

function CameraControls() {
  return <OrbitControls autoRotate autoRotateSpeed={-0.2} />;
}

function ParticleWave() {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);

  const count = 100000; // 粒子の数を大幅に増やす
  const radius = 2; // 半径を少し大きくする
  const height = 6; // 高さを増やす
  const speed = 0.5; // 落下速度を上げる
  const spiralSpeed = 0.5; // 螺旋の回転速度を上げる
  const spiralRadius = 1.5; // 螺旋の半径を大きくする

  // 粒子の初期位置を計算
  const positions = useMemo(() => {
    const positions = [];
    for (let i = 0; i < count; i++) {
      const angle = Math.random() * Math.PI * 2;
      const y = Math.random() * height - height / 2;
      const r = Math.random() * spiralRadius;
      const x = r * Math.cos(angle);
      const z = r * Math.sin(angle);
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, height, spiralRadius]);

  // フレームごとに粒子の位置を更新
  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      // 粒子を下に移動し、螺旋を描く
      const individualSpeed = speed * (0.8 + Math.random() * 0.4);
      y -= individualSpeed * 0.01;
      const angle = Math.atan2(z, x) + spiralSpeed * 0.01;
      const r = Math.sqrt(x * x + z * z);
      x = r * Math.cos(angle);
      z = r * Math.sin(angle);

      // 底に到達したら上に戻す
      if (y < -height / 2) {
        y = height / 2;
        const newAngle = Math.random() * Math.PI * 2;
        const newR = Math.random() * spiralRadius;
        x = newR * Math.cos(newAngle);
        z = newR * Math.sin(newAngle);
      }

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={0xffffff}
        size={0.015} // 粒子のサイズを小さくする
        sizeAttenuation
        transparent={true}
        opacity={0.6} // 透明度を上げる
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function AnimationCanvas() {
  return (
    <Canvas camera={{ position: [0, 0, 8], fov: 75 }}> // カメラを少し後ろに下げる
      <color attach="background" args={['#000']} />
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <CameraControls />
      <ParticleWave />
    </Canvas>
  );
}

function App() {
  return (
    <div className="anim">
      <AnimationCanvas />
    </div>
  );
}

export default App;
