import React, { useRef, useEffect, useState } from 'react';
import { useFrame, useLoader } from '@react-three/fiber';
import * as THREE from 'three';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader';
import { TextGeometry } from 'three/examples/jsm/geometries/TextGeometry';
import circleImg from '../assets/circle.png';



function ParticleText({ text }) {
  const meshRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
//   const font = useLoader(FontLoader, 'src/assets/Baskervville_Regular.json'); // フォントファイルのパスを指定
const font = useLoader(FontLoader, '/Baskervville_Regular.json');
  const [targetPositions, setTargetPositions] = useState([]);

  useEffect(() => {
    if (font && meshRef.current) {
      const geometry = new TextGeometry(text, {
        font: font,
        size: 1,
        height: 0.2,
        curveSegments: 12,
        bevelEnabled: false,
      });
      geometry.center();
      const points = geometry.toNonIndexed().attributes.position.array;
      const positions = [];
      const targets = [];
      for (let i = 0; i < points.length; i += 3) {
        positions.push(
          Math.random() * 10 - 5, // ランダムな初期位置X
          Math.random() * 10 - 5, // ランダムな初期位置Y
          Math.random() * 10 - 5  // ランダムな初期位置Z
        );
        targets.push(points[i], points[i + 1], points[i + 2]);
      }
      meshRef.current.geometry.setAttribute(
        'position',
        new THREE.BufferAttribute(new Float32Array(positions), 3)
      );
      setTargetPositions(targets);
    }
  }, [font, text]);

  useFrame(() => {
    if (meshRef.current && targetPositions.length > 0) {
      const positions = meshRef.current.geometry.attributes.position.array;
      for (let i = 0; i < positions.length; i++) {
        // 目標位置に向かって少しずつ移動
        positions[i] += (targetPositions[i] - positions[i]) * 0.02;
      }
      meshRef.current.geometry.attributes.position.needsUpdate = true;
      meshRef.current.rotation.y += 0.001;
    }
  });

  return (
    <points ref={meshRef}>
      <bufferGeometry />
      <pointsMaterial
        map={imgTex}
        color={0xffa500}
        size={0.05}
        sizeAttenuation
        transparent={true}
        opacity={0.8}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

export default ParticleText;
