import React, { useRef, useMemo, useEffect } from 'react';
import { useFrame } from '@react-three/fiber';
import * as THREE from 'three';

function ParticleText({ text, font, ...props }) {
  const points = useRef();

  const particlesData = useMemo(() => {
    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');
    canvas.width = 1024;
    canvas.height = 256;
    context.font = '200px ' + font;
    context.fillStyle = '#ffffff';
    context.textAlign = 'center';
    context.fillText(text, canvas.width / 2, canvas.height / 2);

    const imageData = context.getImageData(0, 0, canvas.width, canvas.height);
    const data = imageData.data;

    const particles = [];
    for (let y = 0; y < canvas.height; y += 4) {
      for (let x = 0; x < canvas.width; x += 4) {
        if (data[(y * canvas.width + x) * 4 + 3] > 128) {
          particles.push({
            x: (x - canvas.width / 2) / 2,
            y: -(y - canvas.height / 2) / 2,
            z: 0,
            vx: Math.random() * 2 - 1,
            vy: Math.random() * 2 - 1,
            vz: Math.random() * 2 - 1,
          });
        }
      }
    }
    return particles;
  }, [text, font]);

  const positions = useMemo(() => {
    return new Float32Array(particlesData.length * 3);
  }, [particlesData]);

  useEffect(() => {
    if (points.current) {
      points.current.geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));
    }
  }, [positions]);

  useFrame(() => {
    if (points.current) {
      for (let i = 0; i < particlesData.length; i++) {
        const particle = particlesData[i];
        particle.x += particle.vx * 0.1;
        particle.y += particle.vy * 0.1;
        particle.z += particle.vz * 0.1;

        positions[i * 3] = particle.x;
        positions[i * 3 + 1] = particle.y;
        positions[i * 3 + 2] = particle.z;
      }
      points.current.geometry.attributes.position.needsUpdate = true;
    }
  });

  return (
    <points ref={points} {...props}>
      <bufferGeometry>
        <bufferAttribute
          attach="attributes-position"
          count={particlesData.length}
          array={positions}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial color="#ffffff" size={0.05} sizeAttenuation={true} />
    </points>
  );
}

export default ParticleText;
