// App.js
import React, { useRef, useMemo, useEffect, useState } from 'react';
import { Canvas, useFrame, useLoader, useThree } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';
import { createNoise3D } from 'simplex-noise';
import ParticleText from './components/ParticleText';
import { Vector3 } from 'three';

import circleImg from './assets/circle.png';
import './App.css';

// ... 残りのコード ...

function CameraControls() {
  return <OrbitControls autoRotate autoRotateSpeed={-0.2} />;
}

function ParticleWave() {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const noise3D = useMemo(() => createNoise3D(), []);

  const count = 80000;
  const radius = 5;
  const height = 10;
  const speed = 0.8;
  const spiralSpeed = 0.3;
  const flowIntensity = 0.05;

  const [animationState, setAnimationState] = useState('wave');

  const magicCirclePositions = useMemo(() => {
    const positions = [];
    const circleCount = 3;
    const pointsPerCircle = count / circleCount;

    for (let i = 0; i < circleCount; i++) {
      const circleRadius = radius * (i + 1) / circleCount;
      for (let j = 0; j < pointsPerCircle; j++) {
        const angle = (j / pointsPerCircle) * Math.PI * 2;
        const x = circleRadius * Math.cos(angle);
        const z = circleRadius * Math.sin(angle);
        const y = 0; // 上から見た形状なのでy座標は0
        positions.push(new Vector3(x, y, z));
      }
    }
    return positions;
  }, [count, radius]);

  const positions = useMemo(() => {
    const pos = new Float32Array(count * 3);
    for (let i = 0; i < count; i++) {
      const theta = Math.random() * Math.PI * 2;
      const phi = Math.acos(2 * Math.random() - 1);
      const r = Math.pow(Math.random(), 0.3) * radius;
      pos[i * 3] = r * Math.sin(phi) * Math.cos(theta);
      pos[i * 3 + 1] = (Math.random() * 2 - 1) * height * 0.5;
      pos[i * 3 + 2] = r * Math.sin(phi) * Math.sin(theta);
    }
    return pos;
  }, [count, radius, height]);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setAnimationState(prev => prev === 'wave' ? 'magicCircle' : 'wave');
    }, 5000); // 5秒ごとに切り替え

    return () => clearInterval(intervalId);
  }, []);

  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;

    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      if (animationState === 'wave') {
        // 波のアニメーション
        const noise = noise3D(x * 0.5, y * 0.5, z * 0.5 + time * 0.2) * flowIntensity;
        const angle = Math.atan2(z, x) + spiralSpeed * 0.02 + noise;
        const r = Math.sqrt(x * x + z * z);

        x = r * Math.cos(angle);
        z = r * Math.sin(angle);
        y += speed * 0.02 * (1 + noise);

        if (y > height / 2) {
          y = -height / 2;
          const newTheta = Math.random() * Math.PI * 2;
          const newPhi = Math.acos(2 * Math.random() - 1);
          const newR = Math.random() * radius;
          x = newR * Math.sin(newPhi) * Math.cos(newTheta);
          z = newR * Math.sin(newPhi) * Math.sin(newTheta);
        }
      } else {
        // 魔法陣のアニメーション
        const targetPosition = magicCirclePositions[i];
        const lerpFactor = 0.1; // 補間係数（0.0 - 1.0）
        x += (targetPosition.x - x) * lerpFactor;
        y += (targetPosition.y - y) * lerpFactor;
        z += (targetPosition.z - z) * lerpFactor;
      }

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }

    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={0x66ffff}
        size={0.015}
        sizeAttenuation
        transparent={true}
        opacity={0.3}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function ParticleBand({ yPosition, count = 100000, height = 0.4, color = 0x66ffff }) {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const { viewport } = useThree();
  const noise3D = useMemo(() => createNoise3D(), []);

  const positions = useMemo(() => {
    const positions = [];
    const aspect = viewport.width / viewport.height;
    const width = viewport.width * 1.2; // 幅を1.2倍に調整して密度を増加
    const depth = width / aspect;
    for (let i = 0; i < count; i++) {
      const x = (Math.random() - 0.5) * width;
      const y = yPosition + (Math.random() - 0.5) * height * 2; // 高さの範囲をさらに拡大
      const z = (Math.random() - 0.5) * depth;
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, height, yPosition, viewport.width, viewport.height]);

  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    const width = viewport.width * 1.5;
    const aspect = viewport.width / viewport.height;
    const depth = width / aspect;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      // ノイズを使用してより複雑な動きを生成
      const noiseX = noise3D(x * 0.1, y * 0.1, time * 0.1) * 0.02;
      const noiseY = noise3D(y * 0.1, z * 0.1, time * 0.1) * 0.02;
      const noiseZ = noise3D(z * 0.1, x * 0.1, time * 0.1) * 0.02;

      x += noiseX;
      y += noiseY + Math.sin(x * 0.3 + time * 0.2) * 0.005; // 上下の動きを追加
      z += noiseZ;

      // 範囲外に出た粒子をリセット
      if (x < -width / 2 || x > width / 2 || 
          y < yPosition - height || y > yPosition + height || 
          z < -depth / 2 || z > depth / 2) {
        x = (Math.random() - 0.5) * width;
        y = yPosition + (Math.random() - 0.5) * height * 2;
        z = (Math.random() - 0.5) * depth;
      }

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={color}
        size={0.015}
        sizeAttenuation
        transparent={true}
        opacity={0.5} // 不透明度を上げて、より濃く見せる
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function AnimationCanvas() {
  const [canvasSize, setCanvasSize] = useState({ width: window.innerWidth, height: window.innerHeight });
  const [showLogo, setShowLogo] = useState(false);
  const [showButtons, setShowButtons] = useState(false);  // 新しい state を追加
  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setCanvasSize({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener('resize', handleResize);

    const logoTimer = setTimeout(() => setShowLogo(true), 1000);
    const buttonTimer = setTimeout(() => setShowButtons(true), 3000);  // ボタンを遅れて表示

    return () => {
      window.removeEventListener('resize', handleResize);
      clearTimeout(logoTimer);
      clearTimeout(buttonTimer);
    };
  }, []);

  return (
    <>
      <Canvas camera={{ position: [0, 0, 7], fov: 75 }} style={{ width: canvasSize.width, height: canvasSize.height }}>
        <color attach="background" args={['#000']} />
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <CameraControls />
