// App.js
import React, { useRef, useMemo } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';
import circleImg from './assets/circle.png'; // テクスチャのパスを正しく設定
import './App.css';

function CameraControls() {
  return <OrbitControls autoRotate autoRotateSpeed={-0.2} />;
}

function ParticleWave() {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);

  const count = 10000; // 粒子の数を増やす
  const radius = 2; // 螺旋の半径
  const height = 4; // 螺旋の高さ
  const turns = 3; // 螺旋の巻数
  const speed = 0.5; // 粒子の移動速度

  // 粒子の初期位置を計算
  const positions = useMemo(() => {
    const positions = [];
    for (let i = 0; i < count; i++) {
      const t = i / count;
      const angle = 2 * Math.PI * turns * t;
      const x = radius * Math.cos(angle);
      const y = height * (0.5 - t);
      const z = radius * Math.sin(angle);
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, radius, height, turns]);

  // フレームごとに粒子の位置を更新
  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      const t = (i / count + time * speed) % 1;
      const angle = 2 * Math.PI * turns * t;
      positionsArray[i3] = radius * Math.cos(angle);
      positionsArray[i3 + 1] = height * (0.5 - t);
      positionsArray[i3 + 2] = radius * Math.sin(angle);
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={0xffffff}
        size={0.02}
        sizeAttenuation
        transparent={true}
        opacity={0.8}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function AnimationCanvas() {
  return (
    <Canvas camera={{ position: [0, 0, 10], fov: 75 }}>
      <color attach="background" args={['#000']} />
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <CameraControls />
      <ParticleWave />
    </Canvas>
  );
}

function App() {
  return (
    <div className="anim">
      <AnimationCanvas />
    </div>
  );
}

export default App;
