// App.js
import React, { useRef, useMemo } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';
import circleImg from './assets/circle.png'; // テクスチャのパスを正しく設定
import './App.css';

function CameraControls() {
  return <OrbitControls autoRotate autoRotateSpeed={-0.2} />;
}

function ParticleWave() {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg); // テクスチャの読み込み

  const count = 50; // グリッドのサイズ（50x50）
  const separation = 1; // 点と点の間隔
  const amplitude = 2; // 波の振幅を減少
  const frequency = 0.5; // 波の周波数を調整

  // ポイントの初期位置を計算
  const positions = useMemo(() => {
    const positions = [];
    for (let xi = 0; xi < count; xi++) {
      for (let zi = 0; zi < count; zi++) {
        const x = separation * (xi - count / 2);
        const z = separation * (zi - count / 2);
        const y = 0; // 初期Y座標
        positions.push(x, y, z);
      }
    }
    return new Float32Array(positions);
  }, [count, separation]);

  // フレームごとにY座標を更新
  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    let i = 0;
    for (let xi = 0; xi < count; xi++) {
      for (let zi = 0; zi < count; zi++) {
        const x = separation * (xi - count / 2);
        const z = separation * (zi - count / 2);
        // 波の関数（より平面的なサイン波）
        positionsArray[i + 1] = Math.sin((x + z + time * frequency) * 0.5) * amplitude;
        i += 3;
      }
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex} // テクスチャを適用
        color={0xffffff} // 白色に設定
        size={0.1} // 粒子のサイズを小さく設定
        sizeAttenuation
        transparent={true}
        opacity={0.8}
      />
    </points>
  );
}

function AnimationCanvas() {
  return (
    <Canvas camera={{ position: [0, 10, 30], fov: 75 }}>
      <color attach="background" args={['#000']} />
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <CameraControls />
      <axesHelper args={[10]} />
      <gridHelper args={[50, 50]} />
      <ParticleWave />
    </Canvas>
  );
}

function App() {
  return (
    <div className="anim">
      <AnimationCanvas />
    </div>
  );
}

export default App;
