import './App.css';
import * as THREE from 'three';
import {OrbitControls} from 'three/examples/jsm/controls/OrbitControls';
import { Canvas, extend, useFrame, useLoader, useThree } from '@react-three/fiber';
import circleImg from './assets/circle.png';
import { Suspense, useCallback, useMemo, useRef } from 'react';
extend({OrbitControls})


function CameraControls(){
  const {
    camera,
    gl: {domElement}
  } = useThree();

  const controlsRef = useRef();
  useFrame(() => controlsRef.current.update())

  return (
    <orbitControls
      ref={controlsRef}
      args={[camera, domElement]}
      autoRotate
      autoRotateSpeed={-0.2}
    />
  );
}


function MeshSurface() {
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const bufferRef = useRef();

  const tRef = useRef(0);
  const f = 0.002;
  const a = 1;

  const graph = useCallback(
    (x, z) => {
      return Math.sin(f * (x ** 2 + z ** 2 + tRef.current)) * a;
    },
    [f, a]
  );

  const count = 200;
  const sep = 1.5;

  const positions = useMemo(() => {
    const positions = [];
    for (let xi = 0; xi < count; xi++) {
      for (let zi = 0; zi < count; zi++) {
        const x = sep * (xi - count / 2);
        const z = sep * (zi - count / 2);
        const y = graph(x, z);
        positions.push(x, y, z);
      }
    }
    return new Float32Array(positions);
  }, [count, sep, graph]);

  const indices = useMemo(() => {
    const indices = [];
    for (let xi = 0; xi < count - 1; xi++) {
      for (let zi = 0; zi < count - 1; zi++) {
        const a = xi * count + zi;
        const b = xi * count + zi + 1;
        const c = (xi + 1) * count + zi + 1;
        const d = (xi + 1) * count + zi;
        indices.push(a, b, d);
        indices.push(b, c, d);
      }
    }
    return new Uint16Array(indices);
  }, [count]);

  useFrame(() => {
    tRef.current += 15;
    const positionsArray = bufferRef.current.array;
    let i = 0;
    for (let xi = 0; xi < count; xi++) {
      for (let zi = 0; zi < count; zi++) {
        const x = sep * (xi - count / 2);
        const z = sep * (zi - count / 2);
        positionsArray[i + 1] = graph(x, z);
        i += 3;
      }
    }
    bufferRef.current.needsUpdate = true;
  });

  return (
    <mesh>
      <bufferGeometry attach="geometry">
        <bufferAttribute
          ref={bufferRef}
          attachObject={['attributes', 'position']}
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
        <bufferAttribute
          attach="index"
          array={indices}
          count={indices.length}
          itemSize={1}
        />
      </bufferGeometry>

      <meshStandardMaterial
        attach="material"
        map={imgTex}
        color={0xFFFFFF}
        side={THREE.DoubleSide}
        wireframe={false}
      />
    </mesh>
  );
}
function AnimationCanvas() {
  return (
    <Canvas colorManagement={false} camera={{ position: [0, 100, 200], fov: 75 }}>
      <Suspense fallback={null}>
        <MeshSurface />
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} intensity={1} />
        <axesHelper args={[100]} />
        <gridHelper args={[200, 20]} />
      </Suspense>
      <CameraControls />
    </Canvas>
  );
}



function App() {
  return (
    <div className="anim">
      <Suspense fallback={<div>Loading...</div>}>
        <AnimationCanvas />
      </Suspense>
    </div>
  );
}

export default App;
