// App.js
import React, { useRef, useMemo, useEffect, useState } from 'react';
import { Canvas, useFrame, useLoader, useThree } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';
import { createNoise3D } from 'simplex-noise';

import circleImg from './assets/circle.png';
import './App.css';

function CameraControls() {
  return <OrbitControls autoRotate autoRotateSpeed={-0.2} />;
}

function ParticleWave() {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const noise3D = useMemo(() => createNoise3D(), []);
  const timeRef = useRef(0);

  const count = 50000;
  const radius = 4;
  const height = 12;
  const speed = 0.5;
  const spiralSpeed = 0.5;
  const flowIntensity = 0.04;
  const concentrationSpeed = 1.5; // 集約速度を上げる

  const positions = useMemo(() => {
    const positions = [];
    for (let i = 0; i < count; i++) {
      const theta = Math.random() * Math.PI * 2;
      const phi = Math.acos(2 * Math.random() - 1);
      const r = Math.pow(Math.random(), 0.5) * radius * 3; // より広い初期分布
      const x = r * Math.sin(phi) * Math.cos(theta);
      const y = (Math.random() * 2 - 1) * height * 1.5; // より広い高さ分布
      const z = r * Math.sin(phi) * Math.sin(theta);
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, radius, height]);

  useFrame(({ clock }) => {
    const deltaTime = clock.getDelta();
    timeRef.current += deltaTime;
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;

    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      // 集約効果
      const concentrationFactor = Math.min(timeRef.current * concentrationSpeed, 1);
      const targetRadius = radius * (1 - concentrationFactor) + radius * 0.5 * concentrationFactor;
      const currentRadius = Math.sqrt(x * x + z * z);
      const radiusRatio = targetRadius / currentRadius;

      x *= radiusRatio;
      z *= radiusRatio;

      // 螺旋効果
      const spiralFactor = Math.min(timeRef.current * spiralSpeed, 1);
      const spiralAngle = (y / height + 1) * Math.PI * spiralFactor;
      const spiralX = x * Math.cos(spiralAngle) - z * Math.sin(spiralAngle);
      const spiralZ = x * Math.sin(spiralAngle) + z * Math.cos(spiralAngle);

      x = spiralX;
      z = spiralZ;

      const noise = noise3D(x * 0.5, y * 0.5, z * 0.5 + time * 0.2) * flowIntensity;
      y += speed * 0.01 * (1 + noise);

      if (y > height / 2) {
        y = -height / 2;
        const newTheta = Math.random() * Math.PI * 2;
        const newPhi = Math.acos(2 * Math.random() - 1);
        const newR = Math.pow(Math.random(), 0.3) * targetRadius;
        x = newR * Math.sin(newPhi) * Math.cos(newTheta);
        z = newR * Math.sin(newPhi) * Math.sin(newTheta);
      }

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={0x66ffff}
        size={0.025}
        sizeAttenuation
        transparent={true}
        opacity={0.35}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function ParticleBand({ yPosition, count = 300000, height = 0.6, color = 0x66ffff }) {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const { viewport } = useThree();
  const noise3D = useMemo(() => createNoise3D(), []);

  const positions = useMemo(() => {
    const positions = [];
    const aspect = viewport.width / viewport.height;
    const width = viewport.width * 1.5;
    const depth = width / aspect;
    for (let i = 0; i < count; i++) {
      const x = (Math.random() - 0.5) * width;
      const y = yPosition + (Math.random() - 0.5) * height * 2;
      const z = (Math.random() - 0.5) * depth;
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, height, yPosition, viewport.width, viewport.height]);

  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    const width = viewport.width * 1.5;
    const aspect = viewport.width / viewport.height;
    const depth = width / aspect;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      const noiseX = noise3D(x * 0.1, y * 0.1, time * 0.1) * 0.02;
      const noiseY = noise3D(y * 0.1, z * 0.1, time * 0.1) * 0.02;
      const noiseZ = noise3D(z * 0.1, x * 0.1, time * 0.1) * 0.02;

      x += noiseX;
      y += noiseY + Math.sin(x * 0.3 + time * 0.2) * 0.005;
      z += noiseZ;

      if (x < -width / 2 || x > width / 2 || 
          y < yPosition - height || y > yPosition + height || 
          z < -depth / 2 || z > depth / 2) {
        x = (Math.random() - 0.5) * width;
        y = yPosition + (Math.random() - 0.5) * height * 2;
        z = (Math.random() - 0.5) * depth;
      }

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={color}
        size={0.015}
        sizeAttenuation
        transparent={true}
        opacity={0.3}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function AnimationCanvas() {
  const [canvasSize, setCanvasSize] = useState({ width: window.innerWidth, height: window.innerHeight });

  useEffect(() => {
    const handleResize = () => {
      setCanvasSize({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <>
      <Canvas camera={{ position: [0, 0, 7], fov: 75 }} style={{ width: canvasSize.width, height: canvasSize.height }}>
        <color attach="background" args={['#000']} />
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <CameraControls />
        <ParticleWave />
        <ParticleBand yPosition={4.5} color={0x66ffff} count={300000} height={0.6} />
        <ParticleBand yPosition={-4.5} color={0x66ffff} count={300000} height={0.6} />
      </Canvas>
      <div className="overlay-text">
        Babel & Zoltraak
      </div>
    </>
  );
}

function App() {
  return (
    <div className="anim">
      <AnimationCanvas />
    </div>
  );
}

export default App;
