// App.js
import React, { useRef, useMemo, useEffect, useState } from 'react';
import { Canvas, useFrame, useLoader, useThree } from '@react-three/fiber';
import { OrbitControls, Line } from '@react-three/drei';
import * as THREE from 'three';
import { SVGLoader } from 'three/examples/jsm/loaders/SVGLoader';
import { createNoise3D } from 'simplex-noise';
import ParticleText from './components/ParticleText';

import circleImg from './assets/circle.png';
import './App.css';

// ... 残りのコード ...

function CameraControls({ position, up }) {
  const { camera } = useThree();

  useEffect(() => {
    camera.position.set(...position);
    camera.up.set(...up);
    camera.updateProjectionMatrix();
  }, [camera, position, up]);

  return <OrbitControls enableRotate={false} enableZoom={false} enablePan={false} />;
}

function ParticleWave() {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);

  // Define noise3D within the ParticleWave component
  const noise3D = useMemo(() => createNoise3D(), []);

  const count = 80000;
  const radius = 5;
  const height = 10;
  const speed = 0.8; // 速度を上げて、粒子より早く集約するようにする
  const spiralSpeed = 0.3; // スパイラル速度も上げる
  const flowIntensity = 0.05; // フロー強度を少し強める

  const positions = useMemo(() => {
    const positions = [];
    for (let i = 0; i < count; i++) {
      const theta = Math.random() * Math.PI * 2;
      const phi = Math.acos(2 * Math.random() - 1);
      const r = Math.pow(Math.random(), 0.3) * radius; // 0.5から0.3変更してより中心に集中させる
      const x = r * Math.sin(phi) * Math.cos(theta);
      const y = (Math.random() * 2 - 1) * height * 0.5; // 高さ範囲を半分に縮小
      const z = r * Math.sin(phi) * Math.sin(theta);
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, radius, height]);

  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      const noise = noise3D(x * 0.5, y * 0.5, z * 0.5 + time * 0.2) * flowIntensity;
      const angle = Math.atan2(z, x) + spiralSpeed * 0.02 + noise; // スパイラル速度を上げる
      const r = Math.sqrt(x * x + z * z);

      x = r * Math.cos(angle);
      z = r * Math.sin(angle);
      y += speed * 0.02 * (1 + noise); // 上昇速度を上げる

      if (y > height / 2) { // 上限に達したら下に戻す
        y = -height / 2;
        const newTheta = Math.random() * Math.PI * 2;
        const newPhi = Math.acos(2 * Math.random() - 1);
        const newR = Math.random() * radius;
        x = newR * Math.sin(newPhi) * Math.cos(newTheta);
        z = newR * Math.sin(newPhi) * Math.sin(newTheta);
      }

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={0x66ffff}
        size={0.015} // サイズを0.02から0.01に減少
        sizeAttenuation
        transparent={true}
        opacity={0.3}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function ParticleBand({ yPosition, count = 100000, height = 0.4, color = 0x66ffff }) { // height0.4に増加
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const { viewport } = useThree();
  const noise3D = useMemo(() => createNoise3D(), []);

  const positions = useMemo(() => {
    const positions = [];
    const aspect = viewport.width / viewport.height;
    const width = viewport.width * 1.2; // 幅を1.2倍に調整して密度を増加
    const depth = width / aspect;
    for (let i = 0; i < count; i++) {
      const x = (Math.random() - 0.5) * width;
      const y = yPosition + (Math.random() - 0.5) * height * 2; // 高さの範囲をさらに拡大
      const z = (Math.random() - 0.5) * depth;
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, height, yPosition, viewport.width, viewport.height]);

  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    const width = viewport.width * 1.5;
    const aspect = viewport.width / viewport.height;
    const depth = width / aspect;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      // ノイズを使用してより複雑な動きを生成
      const noiseX = noise3D(x * 0.1, y * 0.1, time * 0.1) * 0.02;
      const noiseY = noise3D(y * 0.1, z * 0.1, time * 0.1) * 0.02;
      const noiseZ = noise3D(z * 0.1, x * 0.1, time * 0.1) * 0.02;

      x += noiseX;
      y += noiseY + Math.sin(x * 0.3 + time * 0.2) * 0.005; // 上下の動きを追加
      z += noiseZ;

      // 範囲外に出た粒子をリセット
      if (x < -width / 2 || x > width / 2 || 
          y < yPosition - height || y > yPosition + height || 
          z < -depth / 2 || z > depth / 2) {
        x = (Math.random() - 0.5) * width;
        y = yPosition + (Math.random() - 0.5) * height * 2;
        z = (Math.random() - 0.5) * depth;
      }

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={color}
        size={0.015}
        sizeAttenuation
        transparent={true}
        opacity={0.5} // 不透明度を上げて、より濃く見せる
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function MagicCircle() {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const noise3D = useMemo(() => createNoise3D(), []);

  const count = 100000; // 粒子数を増やす
  const radius = 3;
  const innerRadius = 1.5; // 内側の円の半径

  const positions = useMemo(() => {
    const positions = [];
    for (let i = 0; i < count; i++) {
      const angle = Math.random() * Math.PI * 2;
      const r = Math.sqrt(Math.random()) * (radius - innerRadius) + innerRadius;
      const x = r * Math.cos(angle);
      const z = r * Math.sin(angle);
      const y = (Math.random() - 0.5) * 0.05; // より平面に近づける
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, radius, innerRadius]);

  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      const r = Math.sqrt(x * x + z * z);
      const angle = Math.atan2(z, x);

      // 回転速度を半径に応じて変える
      const rotationSpeed = 0.2 * (1 - (r - innerRadius) / (radius - innerRadius));
      const newAngle = angle + rotationSpeed * 0.02;

      // ズを使って微妙な揺らぎを加える
      const noise = noise3D(x * 0.5, y * 0.5, z * 0.5 + time * 0.2) * 0.01;

      x = r * Math.cos(newAngle) + noise;
      z = r * Math.sin(newAngle) + noise;
      y += Math.sin(time * 2 + r * 2) * 0.001; // 上下の動きを小さくする

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <group>
      <points frustumCulled={false}>
        <bufferGeometry>
          <bufferAttribute
            ref={pointsRef}
            attach="attributes-position"
            array={positions}
            count={positions.length / 3}
            itemSize={3}
          />
        </bufferGeometry>
        <pointsMaterial
          map={imgTex}
          color={0x00ffff} // 青い蛍光色
          size={0.015}
          sizeAttenuation
          transparent={true}
          opacity={0.8}
          blending={THREE.AdditiveBlending}
        />
      </points>
      {/* 魔法陣の装飾的な要素を追加 */}
      <MagicCircleDecorations radius={radius} innerRadius={innerRadius} />
    </group>
  );
}

// 魔法陣の装飾的な要素を追加るコンポーネント
function MagicCircleDecorations({ radius, innerRadius }) {
  const outerCircleRef = useRef();
  const innerCircleRef = useRef();

  useEffect(() => {
    // 外側の円を描画
    const outerCircleGeometry = new THREE.BufferGeometry();
    const outerCircleVertices = [];
    for (let i = 0; i <= 64; i++) {
      const angle = (i / 64) * Math.PI * 2;
      outerCircleVertices.push(radius * Math.cos(angle), 0, radius * Math.sin(angle));
    }
    outerCircleGeometry.setAttribute('position', new THREE.Float32BufferAttribute(outerCircleVertices, 3));
    outerCircleRef.current.geometry = outerCircleGeometry;

    // 内側の円を描画
    const innerCircleGeometry = new THREE.BufferGeometry();
    const innerCircleVertices = [];
    for (let i = 0; i <= 64; i++) {
      const angle = (i / 64) * Math.PI * 2;
      innerCircleVertices.push(innerRadius * Math.cos(angle), 0, innerRadius * Math.sin(angle));
    }
    innerCircleGeometry.setAttribute('position', new THREE.Float32BufferAttribute(innerCircleVertices, 3));
    innerCircleRef.current.geometry = innerCircleGeometry;
  }, [radius, innerRadius]);

  // 星型の形状を作成
  const starShape = useMemo(() => {
    const shape = new THREE.Shape();
    const points = 5;
    const outerRadius = 0.5;
    const innerRadius = 0.2;

    for (let i = 0; i < points * 2; i++) {
      const angle = (i / (points * 2)) * Math.PI * 2;
      const r = i % 2 === 0 ? outerRadius : innerRadius;
      const x = Math.cos(angle) * r;
      const y = Math.sin(angle) * r;
      if (i === 0) {
        shape.moveTo(x, y);
      } else {
        shape.lineTo(x, y);
      }
    }
    shape.closePath();
    return shape;
  }, []);

  return (
    <group>
      <line ref={outerCircleRef}>
        <lineBasicMaterial color={0x00ffff} linewidth={2} />
      </line>
      <line ref={innerCircleRef}>
        <lineBasicMaterial color={0x00ffff} linewidth={2} />
      </line>
      {[0, 60, 120, 180, 240, 300].map((angle, index) => (
        <line key={index}>
          <bufferGeometry>
            <bufferAttribute
              attach="attributes-position"
              count={2}
              array={new Float32Array([
                Math.cos(angle * Math.PI / 180) * innerRadius,
                0,
                Math.sin(angle * Math.PI / 180) * innerRadius,
                Math.cos(angle * Math.PI / 180) * radius,
                0,
                Math.sin(angle * Math.PI / 180) * radius,
              ])}
              itemSize={3}
            />
          </bufferGeometry>
          <lineBasicMaterial color={0x00ffff} linewidth={2} />
        </line>
      ))}
      {/* 魔法陣の中心に星を追加 */}
      <mesh position={[0, 0.01, 0]}>
        <shapeGeometry args={[starShape]} />
        <meshBasicMaterial color={0x00ffff} side={THREE.DoubleSide} />
      </mesh>
    </group>
  );
}

function LineCastle() {
  const [geometries, setGeometries] = useState([]);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const loader = new SVGLoader();
    loader.load('/castle.svg', (data) => {
      const newGeometries = [];
      data.paths.forEach((path) => {
        // ベースのSVGパス
        const basePoints = [];
        path.subPaths.forEach((subPath) => {
          basePoints.push(...subPath.getPoints());
        });
        if (basePoints.length > 0) {
          const baseGeometry = new THREE.BufferGeometry().setFromPoints(basePoints);
          newGeometries.push({ type: 'base', geometry: baseGeometry });
        }

        // サブパス（頂点同士を結ぶ線）
        path.subPaths.forEach((subPath) => {
          const subPoints = subPath.getPoints();
          if (subPoints.length > 1) {
            const subGeometry = new THREE.BufferGeometry().setFromPoints([subPoints[0], subPoints[subPoints.length - 1]]);
            newGeometries.push({ type: 'sub', geometry: subGeometry });
          }
        });
      });
      setGeometries(newGeometries);
    });
  }, []);

  useFrame((state, delta) => {
    setProgress((prev) => Math.min(prev + delta * 0.2, 1));
  });

  const { totalLength, lineDistances } = useMemo(() => {
    let total = 0;
    const distances = [];
    geometries.forEach(({ geometry }) => {
      const positionAttribute = geometry.getAttribute('position');
      for (let i = 1; i < positionAttribute.count; i++) {
        const start = new THREE.Vector3().fromBufferAttribute(positionAttribute, i - 1);
        const end = new THREE.Vector3().fromBufferAttribute(positionAttribute, i);
        const length = start.distanceTo(end);
        total += length;
        distances.push(total);
      }
    });
    return { totalLength: total, lineDistances: distances };
  }, [geometries]);

  useEffect(() => {
    geometries.forEach(({ geometry }) => {
      if (geometry && lineDistances.length > 0) {
        geometry.setAttribute(
          'lineDistance',
          new THREE.BufferAttribute(new Float32Array(lineDistances), 1)
        );
      }
    });
  }, [geometries, lineDistances]);

  const baseMaterial = useMemo(() => {
    return new THREE.ShaderMaterial({
      uniforms: {
        uProgress: { value: progress },
        uTotalLength: { value: totalLength },
        color: { value: new THREE.Color(0x00ffff) },
      },
      vertexShader: `
        attribute float lineDistance;
        varying float vLineDistance;
        void main() {
          vLineDistance = lineDistance;
          gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
        }
      `,
      fragmentShader: `
        uniform float uProgress;
        uniform float uTotalLength;
        uniform vec3 color;
        varying float vLineDistance;
        void main() {
          float alpha = step(vLineDistance / uTotalLength, uProgress);
          gl_FragColor = vec4(color, alpha * 0.3);
        }
      `,
      transparent: true,
    });
  }, [progress, totalLength]);

  const subMaterial = useMemo(() => {
    return new THREE.ShaderMaterial({
      uniforms: {
        uProgress: { value: progress },
        uTotalLength: { value: totalLength },
        color: { value: new THREE.Color(0x00ffff) },
      },
      vertexShader: `
        attribute float lineDistance;
        varying float vLineDistance;
        void main() {
          vLineDistance = lineDistance;
          gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
        }
      `,
      fragmentShader: `
        uniform float uProgress;
        uniform float uTotalLength;
        uniform vec3 color;
        varying float vLineDistance;
        void main() {
          float alpha = step(vLineDistance / uTotalLength, uProgress);
          gl_FragColor = vec4(color, alpha * 0.01);
        }
      `,
      transparent: true,
    });
  }, [progress, totalLength]);

  return (
    <group>
      {geometries.map((item, index) => (
        <line key={index} geometry={item.geometry} material={item.type === 'base' ? baseMaterial : subMaterial} />
      ))}
    </group>
  );
}



