function AnimationCanvas() {
  const [canvasSize, setCanvasSize] = useState({ width: window.innerWidth, height: window.innerHeight });
  const [showLogo, setShowLogo] = useState(false);
  const [showButtons, setShowButtons] = useState(false);
  const [showMagicCircle, setShowMagicCircle] = useState(false);
  const [cameraPosition, setCameraPosition] = useState([0, 0, 7]);
  const [cameraUp, setCameraUp] = useState([0, 1, 0]);
  const [showCastle, setShowCastle] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setCanvasSize({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener('resize', handleResize);

    const logoTimer = setTimeout(() => setShowLogo(true), 1000);
    const buttonTimer = setTimeout(() => setShowButtons(true), 3000);
    const magicCircleTimer = setTimeout(() => {
      setShowMagicCircle(true);
      setCameraPosition([0, 5, 0]); // カメラの高さを増やす
      setCameraUp([0, 0, -1]);
    }, 3000);
    const castleTimer = setTimeout(() => {
      setShowCastle(true);
      setCameraPosition([0, 0, 7]); // カメラ位置をwaveと同じに戻す
      setCameraUp([0, 1, 0]);
    }, 6000); // 魔法陣の後に城を表示

    return () => {
      window.removeEventListener('resize', handleResize);
      clearTimeout(logoTimer);
      clearTimeout(buttonTimer);
      clearTimeout(magicCircleTimer);
      clearTimeout(castleTimer);
    };
  }, []);

  return (
    <>
      <Canvas camera={{ fov: 75 }} style={{ width: canvasSize.width, height: canvasSize.height }}>
        <color attach="background" args={['#000']} />
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <CameraControls position={cameraPosition} up={cameraUp} />
        {!showMagicCircle && <ParticleWave />}
        {showMagicCircle && !showCastle && <MagicCircle />}
        {showCastle && (
          <group position={[-1, 6, -1]} scale={[0.02, -0.02, 0.02]}>
            <LineCastle />
          </group>
        )}
        <ParticleBand yPosition={4.5} color={0x66ffff} count={50000} height={0.4} />
        <ParticleBand yPosition={-4.5} color={0x66ffff} count={50000} height={0.4} />
      </Canvas>
      <div className="overlay-content">
        <h1
          className={`logo ${showLogo ? 'visible' : ''}`}
        >
          Babel & Zoltraak
        </h1>
        <h2 className={`leed ${showLogo ? 'visible' : ''}`}>
          言語を超え、文化を繋ぐ 新たな世界の創造へ
        </h2>
        <div className={`button-container ${showButtons ? 'visible' : ''}`}>
          <button className="action-button">Babel Editor</button>
          <button className="action-button">System List</button>
        </div>
      </div>
    </>
  );
}

function App() {
  return (
    <div className="anim">
      <AnimationCanvas />
    </div>
  );
}

export default App;




