// App.js
import React, { useRef, useMemo } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';
import { createNoise3D } from 'simplex-noise'; // この行は変更なし
import circleImg from './assets/circle.png';
import './App.css';

// simplex オブジェクトを作成
const noise3D = createNoise3D(); // この行を変更

function CameraControls() {
  return <OrbitControls autoRotate autoRotateSpeed={-0.2} />;
}

function ParticleWave() {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);

  const count = 150000; // 粒子数をさらに増やす
  const radius = 2.5;
  const height = 8;
  const speed = 0.8;
  const spiralSpeed = 0.3;
  const flowIntensity = 0.05;

  // 粒子の初期位置を計算
  const positions = useMemo(() => {
    const positions = [];
    for (let i = 0; i < count; i++) {
      const theta = Math.random() * Math.PI * 2;
      const phi = Math.acos(2 * Math.random() - 1);
      const r = Math.random() * radius;
      const x = r * Math.sin(phi) * Math.cos(theta);
      const y = Math.random() * height - height / 2;
      const z = r * Math.sin(phi) * Math.sin(theta);
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, radius, height]);

  // フレームごとに粒子の位置を更新
  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      // エネルギーの流れを表現する動き
      const noise = noise3D(x * 0.5, y * 0.5, z * 0.5 + time * 0.2) * flowIntensity; // この行を変更
      const angle = Math.atan2(z, x) + spiralSpeed * 0.01 + noise;
      const r = Math.sqrt(x * x + z * z);

      x = r * Math.cos(angle);
      z = r * Math.sin(angle);
      y -= speed * 0.01 * (1 + noise);

      // 中心に向かう力を加える
      const distanceToCenter = Math.sqrt(x * x + z * z);
      const centerForce = 0.001 * distanceToCenter;
      x -= x * centerForce;
      z -= z * centerForce;

      // 底に到達したら上に戻す
      if (y < -height / 2) {
        y = height / 2;
        const newTheta = Math.random() * Math.PI * 2;
        const newPhi = Math.acos(2 * Math.random() - 1);
        const newR = Math.random() * radius;
        x = newR * Math.sin(newPhi) * Math.cos(newTheta);
        z = newR * Math.sin(newPhi) * Math.sin(newTheta);
      }

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={0x00ffff} // エネルギーっぽい色に変更
        size={0.02}
        sizeAttenuation
        transparent={true}
        opacity={0.6}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function ParticleBand({ yPosition, count = 50000, width = 5, height = 0.5, color = 0x00ffff }) {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);

  const positions = useMemo(() => {
    const positions = [];
    for (let i = 0; i < count; i++) {
      const x = (Math.random() - 0.5) * width;
      const y = yPosition + (Math.random() - 0.5) * height;
      const z = (Math.random() - 0.5) * width;
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, width, height, yPosition]);

  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      // 粒子を水平方向に動かす
      x += Math.sin(z * 0.5 + time * 0.5) * 0.01;
      z += Math.cos(x * 0.5 + time * 0.5) * 0.01;

      // 範囲外に出たら反対側に戻す
      if (x < -width / 2) x += width;
      if (x > width / 2) x -= width;
      if (z < -width / 2) z += width;
      if (z > width / 2) z -= width;

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={color}
        size={0.02}
        sizeAttenuation
        transparent={true}
        opacity={0.6}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function AnimationCanvas() {
  return (
    <Canvas camera={{ position: [0, 0, 8], fov: 75 }}> // カメラを少し後ろに下げる
      <color attach="background" args={['#000']} />
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <CameraControls />
      <ParticleWave />
      <ParticleBand yPosition={4} color={0x00ffff} /> {/* 上部の粒子帯 */}
      <ParticleBand yPosition={-4} color={0x00ffff} /> {/* 下部の粒子帯 */}
    </Canvas>
  );
}

function App() {
  return (
    <div className="anim">
      <AnimationCanvas />
    </div>
  );
}

export default App;
