// App.js
import React, { useRef, useMemo, useEffect, useState } from 'react';
import { Canvas, useFrame, useLoader, useThree } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader.js';
import { TextGeometry } from 'three/examples/jsm/geometries/TextGeometry.js';
import { createNoise3D } from 'simplex-noise';
import circleImg from './assets/circle.png';

import './App.css';

const noise3D = createNoise3D();

function CameraControls() {
  return <OrbitControls autoRotate autoRotateSpeed={-0.2} />;
}

function ParticleWave() {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);

  const count = 100000;
  const radius = 2;
  const height = 7;
  const speed = 0.8;
  const spiralSpeed = 0.3;
  const flowIntensity = 0.05;

  const positions = useMemo(() => {
    const positions = [];
    for (let i = 0; i < count; i++) {
      const theta = Math.random() * Math.PI * 2;
      const phi = Math.acos(2 * Math.random() - 1);
      const r = Math.random() * radius;
      const x = r * Math.sin(phi) * Math.cos(theta);
      const y = Math.random() * height - height / 2;
      const z = r * Math.sin(phi) * Math.sin(theta);
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, radius, height]);

  const opacities = useMemo(() => {
    const opacities = [];
    for (let i = 0; i < count; i++) {
      const y = positions[i * 3 + 1];
      const baseOpacity = 0.3;
      const edgeOpacity = 0.6;
      const opacity = baseOpacity + (edgeOpacity - baseOpacity) * (1 - Math.abs(y / (height / 2)));
      opacities.push(opacity, opacity, opacity);
    }
    return new Float32Array(opacities);
  }, [positions, count, height]);

  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    const opacitiesArray = pointsRef.current.attributes.opacity.array;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      const noise = noise3D(x * 0.5, y * 0.5, z * 0.5 + time * 0.2) * flowIntensity;
      const angle = Math.atan2(z, x) + spiralSpeed * 0.01 + noise;
      const r = Math.sqrt(x * x + z * z);

      x = r * Math.cos(angle);
      z = r * Math.sin(angle);
      y += speed * 0.01 * (1 + noise);

      if (y > height / 2) {
        y = -height / 2;
        const newTheta = Math.random() * Math.PI * 2;
        const newPhi = Math.acos(2 * Math.random() - 1);
        const newR = Math.random() * radius;
        x = newR * Math.sin(newPhi) * Math.cos(newTheta);
        z = newR * Math.sin(newPhi) * Math.sin(newTheta);
      }

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;

      const opacity = opacities[i3];
      opacitiesArray[i3] = opacity;
      opacitiesArray[i3 + 1] = opacity;
      opacitiesArray[i3 + 2] = opacity;
    }
    pointsRef.current.needsUpdate = true;
    pointsRef.current.attributes.opacity.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
        <bufferAttribute
          attach="attributes-opacity"
          array={opacities}
          count={opacities.length / 3}
          itemSize={1}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={0x66ffff}
        size={0.02}
        sizeAttenuation
        transparent={true}
        opacity={0.3}
        blending={THREE.AdditiveBlending}
        vertexColors
      />
    </points>
  );
}

function ParticleBand({ yPosition, count = 200000, height = 0.2, color = 0x66ffff, opacity = 0.2, size = 0.015 }) {
  const pointsRef = useRef();
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const { viewport } = useThree();

  const positions = useMemo(() => {
    const positions = [];
    const aspect = viewport.width / viewport.height;
    const width = viewport.width * 1.2;
    const depth = width / aspect;
    for (let i = 0; i < count; i++) {
      const x = (Math.random() - 0.5) * width;
      const y = yPosition + (Math.random() - 0.5) * height;
      const z = (Math.random() - 0.5) * depth;
      positions.push(x, y, z);
    }
    return new Float32Array(positions);
  }, [count, height, yPosition, viewport.width, viewport.height]);

  useFrame(({ clock }) => {
    const time = clock.getElapsedTime();
    const positionsArray = pointsRef.current.array;
    const width = viewport.width * 1.2;
    const aspect = viewport.width / viewport.height;
    const depth = width / aspect;
    for (let i = 0; i < count; i++) {
      const i3 = i * 3;
      let x = positionsArray[i3];
      let y = positionsArray[i3 + 1];
      let z = positionsArray[i3 + 2];

      x += Math.sin(z * 0.3 + time * 0.2) * 0.01;
      z += Math.cos(x * 0.3 + time * 0.2) * 0.01;
      y += Math.sin(x * 0.3 + z * 0.3 + time * 0.1) * 0.002;

      if (x < -width / 2) x += width;
      if (x > width / 2) x -= width;
      if (z < -depth / 2) z += depth;
      if (z > depth / 2) z -= depth;

      positionsArray[i3] = x;
      positionsArray[i3 + 1] = y;
      positionsArray[i3 + 2] = z;
    }
    pointsRef.current.needsUpdate = true;
  });

  return (
    <points frustumCulled={false}>
      <bufferGeometry>
        <bufferAttribute
          ref={pointsRef}
          attach="attributes-position"
          array={positions}
          count={positions.length / 3}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        map={imgTex}
        color={color}
        size={size}
        sizeAttenuation
        transparent={true}
        opacity={opacity}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function TextParticles() {
  const [dragging, setDragging] = useState(false);
  const pointsRef = useRef();
  const originalPositions = useRef();
  const targetPositions = useRef();
  const mousePosition = useRef({ x: 0, y: 0 });
  const { viewport } = useThree();

  // フォントをロード
  const font = useLoader(FontLoader, '/assets/Baskervville_Italic.json'); // フォントファイルのパスを確認

  // テキストジオメトリを作成
  const textGeometry = useMemo(() => {
    const geometry = new TextGeometry('Babel & Zoltraak', {
      font: font,
      size: 1,
      height: 0.1,
    });
    geometry.center();
    return geometry;
  }, [font]);

  const particleCount = 5000;

  // テキストジオメトリから粒子の位置を設定
  const positions = useMemo(() => {
    const tempPositions = textGeometry.attributes.position.array;
    const positionsArray = new Float32Array(particleCount * 3);
    for (let i = 0; i < particleCount; i++) {
      const i3 = i * 3;
      const randomIndex = Math.floor(Math.random() * (tempPositions.length / 3)) * 3;
      positionsArray[i3] = tempPositions[randomIndex];
      positionsArray[i3 + 1] = tempPositions[randomIndex + 1];
      positionsArray[i3 + 2] = tempPositions[randomIndex + 2];
    }
    originalPositions.current = positionsArray.slice();
    targetPositions.current = positionsArray.slice();
    return positionsArray;
  }, [textGeometry, particleCount]);

  useFrame(() => {
    const positionsArray = pointsRef.current.geometry.attributes.position.array;

    for (let i = 0; i < particleCount; i++) {
      const i3 = i * 3;

      if (dragging) {
        const dx = mousePosition.current.x - positionsArray[i3];
        const dy = mousePosition.current.y - positionsArray[i3 + 1];
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < 0.1) {
          positionsArray[i3] += (Math.random() - 0.5) * 0.1;
          positionsArray[i3 + 1] += (Math.random() - 0.5) * 0.1;
          positionsArray[i3 + 2] += (Math.random() - 0.5) * 0.1;
        }
      } else {
        const targetX = targetPositions.current[i3];
        const targetY = targetPositions.current[i3 + 1];
        const targetZ = targetPositions.current[i3 + 2];
        positionsArray[i3] += (targetX - positionsArray[i3]) * 0.1;
        positionsArray[i3 + 1] += (targetY - positionsArray[i3 + 1]) * 0.1;
        positionsArray[i3 + 2] += (targetZ - positionsArray[i3 + 2]) * 0.1;
      }
    }

    pointsRef.current.geometry.attributes.position.needsUpdate = true;
  });

  const handlePointerDown = (event) => {
    setDragging(true);
    updateMousePosition(event);
  };

  const handlePointerUp = () => {
    setDragging(false);
    targetPositions.current = originalPositions.current.slice();
  };

  const handlePointerMove = (event) => {
    if (dragging) {
      updateMousePosition(event);
    }
  };

  const updateMousePosition = (event) => {
    mousePosition.current.x = (event.clientX / window.innerWidth) * 2 - 1;
    mousePosition.current.y = -(event.clientY / window.innerHeight) * 2 + 1;
  };

  return (
    <points
      ref={pointsRef}
      onPointerDown={handlePointerDown}
      onPointerUp={handlePointerUp}
      onPointerMove={handlePointerMove}
    >
      <bufferGeometry>
        <bufferAttribute
          attach="attributes-position"
          count={positions.length / 3}
          array={positions}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        color={0xffffff}
        size={0.02}
        sizeAttenuation
        transparent
        opacity={0.8}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
}

function AnimationCanvas() {
  const [canvasSize, setCanvasSize] = useState({ width: window.innerWidth, height: window.innerHeight });

  useEffect(() => {
    const handleResize = () => {
      setCanvasSize({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <Canvas camera={{ position: [0, 0, 9], fov: 75 }} style={{ width: canvasSize.width, height: canvasSize.height }}>
      <color attach="background" args={['#000']} />
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <CameraControls />
      <ParticleWave />
      <ParticleBand yPosition={4.5} color={0x66ffff} count={200000} />
      <ParticleBand 
        yPosition={-4.5} 
        color={0x66ffff} 
        count={250000}
        opacity={0.3}
        size={0.018}
      />
      <TextParticles />
    </Canvas>
  );
}

function App() {
  return (
    <div className="anim">
      <AnimationCanvas />
    </div>
  );
}

export default App;
