// ./src/App.js
import './App.css';
import * as THREE from 'three';
import { Suspense, useRef } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import circleImg from './assets/circle.png'; // テクスチャのインポート

function CameraControls() {
  return <OrbitControls autoRotate autoRotateSpeed={-0.2} />;
}

function MeshSurface() {
  const imgTex = useLoader(THREE.TextureLoader, circleImg);
  const meshRef = useRef();

  const tRef = useRef(0);
  const speed = 2; // 波の速度
  const frequency = 0.2; // 波の周波数（調整）
  const amplitude = 1.5; // 波の振幅（調整）

  useFrame((state, delta) => {
    tRef.current += delta * speed;

    const positions = meshRef.current.geometry.attributes.position.array;
    const vertex = new THREE.Vector3();

    for (let i = 0; i < positions.length; i += 3) {
      vertex.fromArray(positions, i);
      const distance = Math.sqrt(vertex.x * vertex.x + vertex.z * vertex.z);
      positions[i + 1] = Math.sin(distance * frequency - tRef.current) * amplitude;
    }

    meshRef.current.geometry.attributes.position.needsUpdate = true;
    meshRef.current.geometry.computeVertexNormals();
  });

  return (
    <mesh ref={meshRef} rotation={[-Math.PI / 2, 0, 0]}>
      <planeGeometry args={[300, 300, 100, 100]} />
      <meshStandardMaterial
        map={imgTex}
        color={0xFFFFFF}
        side={THREE.DoubleSide}
        wireframe={false}
      />
    </mesh>
  );
}

function AnimationCanvas() {
  return (
    <Canvas
      colorManagement={false}
      camera={{ position: [0, 100, 200], fov: 75 }}
    >
      <Suspense fallback={null}>
        <MeshSurface />
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} intensity={1} />
        <axesHelper args={[100]} />
        <gridHelper args={[300, 30]} />
      </Suspense>
      <CameraControls />
    </Canvas>
  );
}

function App() {
  return (
    <div className="anim">
      <Suspense fallback={<div>Loading...</div>}>
        <AnimationCanvas />
      </Suspense>
    </div>
  );
}

export default App;
